mapper.select:
SELECT event_id, event_title, event_sub, event_start, event_end, event_thumbnail, event_tag, event_image, event_type, banner
FROM event
WHERE event_id=#{value};

SELECT * FROM (SELECT ROW_NUMBER() OVER(ORDER BY event.event_end DESC) num, event.* FROM event
WHERE event_type=0 AND event_end >= CURRENT_DATE()
WHERE event_type=1
WHERE event_end <= CURRENT_DATE()
) temp WHERE num BETWEEN #{startRowNum} AND #{endRowNum}
