SELECT * FROM (SELECT ROW_NUMBER() OVER(ORDER BY images.image_id) num, images.* FROM images, keywords, feeds WHERE images.image_id = keywords.image_id AND feeds.feed_id = images.feed_id AND images.is_thumbnail = 0 AND feeds.feed_state = 0
AND keywords.keyword_en LIKE CONCAT('%',#{keyword},'%')
GROUP BY images.image_id ) temp WHERE num BETWEEN #{startRowNum} AND #{endRowNum}

