SELECT * FROM (SELECT ROW_NUMBER() OVER(ORDER BY images.image_id) num, images.* FROM images, keywords, feeds WHERE images.image_id = keywords.image_id AND feeds.feed_id = images.feed_id AND images.is_thumbnail = 0 AND feeds.feed_state = 0
AND UPPER(keywords.keyword_en) LIKE CONCAT('%',#{keyword},'%')
GROUP BY images.image_id ) temp WHERE num BETWEEN #{startRowNum} AND #{endRowNum}

SELECT * FROM (SELECT ROW_NUMBER() OVER(ORDER BY temp.same DESC) num, temp.* FROM (SELECT COUNT(images.image_id)same, images.* FROM images, keywords, feeds WHERE images.image_id = keywords.image_id AND feeds.feed_id = images.feed_id AND images.is_thumbnail = 0 AND feeds.feed_state = 0 AND UPPER(keywords.keyword_en) IN
#{item.keywordEn}
GROUP BY images.image_id ) temp) tab WHERE num BETWEEN #{startRowNum} AND #{endRowNum}

SELECT images.* FROM images, keywords, feeds WHERE images.image_id = keywords.image_id AND feeds.feed_id = images.feed_id AND images.is_thumbnail = 0 AND feeds.feed_state = 0
AND UPPER(keywords.keyword_en) = #{item.keywordEn}
GROUP BY images.image_id
